-- Comprehensive K53 Questions for Supabase Database
-- This script adds 100+ questions to ensure a full question pool for exams
-- Run this in Supabase SQL Editor after previous seed scripts

INSERT INTO questions (id, category, learner_code, question_text, options, correct_index, explanation, version, is_active, difficulty_level, created_at, updated_at)
SELECT 
  gen_random_uuid(),
  data.category,
  data.learner_code,
  data.question_text,
  data.options,
  data.correct_index,
  data.explanation,
  1,
  true,
  data.difficulty_level,
  NOW(),
  NOW()
FROM (
  VALUES
    -- Rules of the Road - Comprehensive Questions (40 questions)
    ('rules_of_road', 1, 'What should you do when you see a school bus with flashing red lights?', '[{"text": "Pass quickly"}, {"text": "Stop and wait until lights stop flashing"}, {"text": "Sound your horn"}, {"text": "Switch lanes"}]'::jsonb, 1, 'You must stop for a school bus with flashing red lights to allow children to board or alight safely.', 1),
    ('rules_of_road', 2, 'What is the speed limit in urban areas unless otherwise indicated?', '[{"text": "40 km/h"}, {"text": "60 km/h"}, {"text": "80 km/h"}, {"text": "100 km/h"}]'::jsonb, 1, 'The default speed limit in urban areas is 60 km/h unless signs indicate otherwise.', 1),
    ('rules_of_road', 3, 'When approaching a traffic officer directing traffic, you must:', '[{"text": "Ignore them and follow traffic lights"}, {"text": "Obey the traffic officer''s signals"}, {"text": "Sound your horn"}, {"text": "Speed up"}]'::jsonb, 1, 'Traffic officers'' signals override traffic lights and signs. You must obey their directions.', 1),
    ('rules_of_road', 1, 'What does a solid white line along the side of the road indicate?', '[{"text": "Overtaking allowed"}, {"text": "No stopping or parking"}, {"text": "Pedestrian crossing"}, {"text": "Speed bump ahead"}]'::jsonb, 1, 'A solid white line along the road edge indicates no stopping or parking in that area.', 1),
