    ('rules_of_road', 3, 'What should you do when merging onto a freeway?', '[{"text": "Stop and wait for gap"}, {"text": "Adjust speed to match traffic and merge safely"}, {"text": "Force your way in"}, {"text": "Use the emergency lane"}]'::jsonb, 1, 'When merging, adjust your speed to match freeway traffic and find a safe gap to merge into.', 2),
    ('rules_of_road', 1, 'What is the minimum following distance in ideal conditions?', '[{"text": "1 second"}, {"text": "2 seconds"}, {"text": "3 seconds"}, {"text": "4 seconds"}]'::jsonb, 1, 'Maintain at least a 2-second following distance in good conditions to allow time to react.', 1),
    ('rules_of_road', 2, 'When must you dip your headlights?', '[{"text": "When approaching oncoming traffic"}, {"text": "Only in urban areas"}, {"text": "Never"}, {"text": "During daylight"}]'::jsonb, 0, 'Dip your headlights when approaching oncoming traffic to avoid dazzling other drivers.', 1),
    ('rules_of_road', 3, 'What does a green traffic light mean?', '[{"text": "Speed up"}, {"text": "Proceed if the way is clear"}, {"text": "Stop"}, {"text": "Prepare to stop"}]'::jsonb, 1, 'A green light means you may proceed if the intersection is clear and it is safe to do so.', 1),
    ('rules_of_road', 1, 'When should you use your indicators?', '[{"text": "Only when turning"}, {"text": "When changing lanes or turning"}, {"text": "When driving straight"}, {"text": "Never"}]'::jsonb, 1, 'Use indicators when turning, changing lanes, or when required to warn other road users of your intentions.', 1),
    ('rules_of_road', 2, 'What is the rule for overtaking on the left?', '[{"text": "Never allowed"}, {"text": "Allowed when vehicle in front is turning right"}, {"text": "Always allowed"}, {"text": "Only on freeways"}]'::jsonb, 1, 'You may overtake on the left if the vehicle in front is turning right and there is sufficient space.', 2),
    ('rules_of_road', 3, 'What should you do at a four-way stop?', '[{"text": "The first to arrive goes first"}, {"text": "The largest vehicle goes first"}, {"text": "Always wait your turn"}, {"text": "Proceed immediately"}]'::jsonb, 0, 'At a four-way stop, the vehicle that arrives first has the right of way. If simultaneous, yield to the right.', 2),
    ('rules_of_road', 1, 'When must you yield to pedestrians?', '[{"text": "Only at crossings"}, {"text": "Always when they are crossing"}, {"text": "Never"}, {"text": "Only in residential areas"}]'::jsonb, 1, 'You must yield to pedestrians at crossings and when they are crossing the road at intersections.', 1),
