    ('rules_of_road', 3, 'When may you drive in the right lane of a freeway?', '[{"text": "Never"}, {"text": "When overtaking or turning right"}, {"text": "Always"}, {"text": "When towing"}]'::jsonb, 1, 'Use the right lane for overtaking or when preparing to turn right. Otherwise, keep left.', 2),
    ('rules_of_road', 1, 'What should you do if your vehicle breaks down on a freeway?', '[{"text": "Stay in the vehicle"}, {"text": "Move to a safe place and call for help"}, {"text": "Walk to find help"}, {"text": "Try to repair it yourself"}]'::jsonb, 1, 'If possible, move to a safe location off the road and call for assistance. Use hazard lights.', 2),
    ('rules_of_road', 2, 'What is the legal requirement for seat belts?', '[{"text": "Only driver must wear"}, {"text": "All occupants must wear seat belts"}, {"text": "Only in front seats"}, {"text": "Not required"}]'::jsonb, 1, 'All occupants must wear seat belts if they are available in the vehicle.', 1),
